const cart=document.querySelector(".cart"),fav=document.querySelector(".favorite"),cartLinks=document.querySelectorAll(".products-buy-button"),cartPopup=document.querySelector(".modal-cart"),cartClose=cartPopup.querySelector(".modal-close"),countCart=document.querySelector("#count-cart"),countFav=document.querySelector("#count-favorites"),favLinks=document.querySelectorAll(".products-fav-button");cartLinks.forEach(a=>{a.addEventListener("click",function(a){a.preventDefault(),cartPopup.classList.add("modal-show"),cart.classList.add("cart-fool"),countCart.textContent=+countCart.textContent+1})}),cartClose.addEventListener("click",function(a){a.preventDefault(),cartPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&cartPopup.classList.contains("modal-show")&&(a.preventDefault(),cartPopup.classList.remove("modal-show"))}),favLinks.forEach(a=>{a.addEventListener("click",function(a){a.preventDefault(),fav.classList.add("favorite-add"),countFav.textContent=+countFav.textContent+1})});
